import React, { useContext,useState } from "react";
import Card from "react-bootstrap/Card";
import ApiContext from "./contexts/ApiContext";

function InventoryManagerPage() {
  const apiContextData = useContext(ApiContext);
  let inventoryItems = apiContextData[0].inventoryData;

  function printInv() {
    return inventoryItems.map((invItem) => (
      <div>
        <Card>
          <Card.Body>
              invItem.inventoryName
              invItem.inventoryImg
              invItem.inventoryPrice
          </Card.Body>
        </Card>
      </div>
    ));
  }

  return (
    <div>
        {inventoryItems ? printInv(): "Loadi"};
    </div>
  );
}

export default InventoryManagerPage;
