import React, { useEffect, useState, useContext } from "react";
import Card from "react-bootstrap/Card";
import ApiContext from "./contexts/ApiContext";
import axios from "axios";
import { precartObj } from "./objects/precartItems";
import { Col, Row, Container, Button } from "react-bootstrap";

function ItemCard({ getCart }) {
  //! ─── States ──────────────────────────────────────────────────────────────────
  // const [nameOfCartItem, setNameOfCartItem] = useState("");
  // const [priceOfCartItem, setPriceOfCartItem] = useState("");
  const [cartObject, setCartObject] = useState({ cartName: "", cartPrice: "" });
  const [cart, setCart] = useState(null);

  //! ─── Context ──────────────────────────────────────────────────────────────────
  const apiContextData = useContext(ApiContext);

  //! ─── Variables ──────────────────────────────────────────────────────────────────
  let precartArray = precartObj;

  let cartItems = apiContextData[0].cartData;
  console.log(cartItems);

  useEffect(() => {
    getCart();
  }, []);

  async function postPhone(e,nameOfCartItem,priceOfCartItem) {
    e.preventDefault();
    try {
      // nameOfPhone ? firstPhone() : 'Loading...';
      cartObject.cartName = nameOfCartItem;
      cartObject.cartPrice = priceOfCartItem;

      const res = await axios.post("http://localhost:8281/cart", cartObject);
      console.log(res.data);

      const secondRes = await axios.get("http://localhost:8281/cart");
      setCart(secondRes.data);
      getCart(); //! Added this to see if refreshes
    } catch (e) {
      console.error(e, e.message);
    }
  }

  function printPrecart(precartObj) {
    return precartObj.map((precartItem) => (
      <div>
        <Col>
          <Card
            border="info"
            style={{ background: "black", color: "red", width: "18rem" }}
          >
            <Card.Img src={precartItem.precartImg} />
            <Card.Header>{precartItem.precartName}</Card.Header>
            <Card.Body>
              <Card.Title>{precartItem.precartPrice}</Card.Title>
              <Button col onClick={(e) => postPhone(e,precartItem.precartName,precartItem.precartPrice)}>Add to Item</Button>
              <Card.Text>
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </div>
    ));
  }

  function printItems() {
    return cartItems.map((cartItem) => (
      <div>
        <Card
          border="info"
          style={{ background: "black", color: "red", width: "18rem" }}
        >
          <Card.Img src="holder.js/100px270" alt="Card image" />
          <Card.Header>{cartItem.cartName}</Card.Header>
          <Card.Body>
            <Card.Title>{cartItem.cartId}</Card.Title>
            <button onClick={(e) => postPhone(e)}>Add to Item</button>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
          </Card.Body>
        </Card>
      </div>
    ));
  }

  // <button id={ idParam} onClick={ (e) => delElement(e.target.id) }>Remove Item</button>

  return (
    <div>
      {/* <Card
        border="info"
        style={{ background: "black", color: "red", width: "18rem" }}
      >
        <Card.Img src="holder.js/100px270" alt="Card image" />
        <Card.Header>{cartItems[0].cartName}</Card.Header>
        <Card.Body>
          <Card.Title>{cartItems[0].cartId}</Card.Title>

          <Card.Text>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </Card.Text>
        </Card.Body>
      </Card> */}
      {/* <button id={cartItem.cartId} onClick={(e) => delElement(e.target.id)}>
            Remove Item
          </button> */}
      {/* {cart ? apiContextData[0].setCartData(cart):null} */}
      {/* {printItems()} */}
      <Container>
        <Row>{printPrecart(precartArray)}</Row>
      </Container>
    </div>
  );
}

export default ItemCard;
